import React, {useState} from "react";
let
url1 = "https://www.chimerarevo.com/wp-content/uploads/2020/04/immagini-gratis.jpg",
url2 = "https://i1.wp.com/www.giacomocusano.com/wp-content/uploads/2016/07/coastal-wash-web.jpg?fit=1024%2C682&ssl=1",
url3 = "https://www.artemedialab.it/wp-content/uploads/2019/04/immagini-sfondo-1-700x400.jpg",
url4 = "https://www.radiomontecarlo.net/resizer/1200/720/true/cosmo-1592899369678.jpg--una_meravigliosa_farfalla_cosmica_si_aggira_nello_spazio__le_immagini_sono_mozzafiato.jpg?1592899369000";

const Slider = () => {
 const [index, setIndex] = useState(0)
 const images = [ url1, url2, url3, url4]
 const totImgs = images.lenght-1;
 const back = () => {if (index > 0)
    {setIndex(index-1)}
    else
    {setIndex(totImgs)}}
const forward = () => {
    if (index < totImgs)
    {setIndex(index + 1)}
    else 
    {setIndex(0)}
}

return(
    <div>

        {images.map((src,i)=> {
            const isVisble = index === i
            return <img src = {src} alt="showroom" style= {{opacity: isVisible ? i :0}}/>}

        )}
        <button name="" id="indietro" class="btn btn-back" onClick={back} > back </button>
         <button name="" id="avanti" class="btn btn-fw" onClick={forward}> avanti </button>
    </div>)
}

export default Slider;
