import React from "react";
import styled from "styled-components";
import { Link  } from 'react-router-dom'
import useToggle from "@rooks/use-toggle";

const Burger = styled.div`
@media screen and {max-width:1420}
{
#menu__toggle {
  opacity: 0;

}
#menu__toggle:checked + .menu__btn > span {
  transform: rotate(45deg);
  background-color:#1d1d1d;
}
#menu__toggle:checked + .menu__btn > span::before {
  top: 0;
  transform: rotate(0deg);
  background-color:#1d1d1d;

}
#menu__toggle:checked + .menu__btn > span::after {
  top: 0;
  transform: rotate(90deg);
  background-color:#1d1d1d;

}
#menu__toggle:checked ~ .menu__box {
  left: 80% !important;
}
.menu__btn {
  position: absolute;
  top: 48px;
  left: 96%;
  width: 46px;
  height: 46px;
  cursor: pointer;
  border:none;
  z-index: 1;
}
.menu__btn > span,
.menu__btn > span::before,
.menu__btn > span::after {
  display: block;
  position: absolute;
  width: 100%;
  border:solid 0.01em;
  height: 4px;
  background-color: white;
  transition-duration: .25s;

}
.menu__btn > span::before {
  content: '';

  top: -8px;
}
.menu__btn > span::after {
  content: '';
  top: 8px;
}
.menu__box {
  display: block;
  position: fixed;
  top: 0;
  right: 0;
  width: 300px;
  height: 100%;
  margin: 0;
  padding: 80px 0;
  list-style: none;
  background-color: rgba(246, 246, 246, .94);
  box-shadow: 2px 2px 6px rgba(0, 0, 0, .4);
  font-family:"poppins";
  font-size: 120%;
  transition-duration: .25s;
}
.menu__item {
  display: block;
  padding: 2% 2%;
  color: white;
  font-family: "poppins";
  font-size:180%;

  font-weight: 600;
  text-decoration: none;
  transition-duration: .25s;
}
.menu__item:hover {
  background-color: #1d1d1d;
  color:#f6f6f6;
}
}
`

function Ham(){
  const [show, toggleShow] =  useToggle(false)
 return (  
<Burger>
< div className="hamburger-menu">
    <input id="menu__toggle" type="checkbox" onClick={toggleShow}  />
    <label className="menu__btn" for="menu__toggle">
      <span></span>
    </label>

    <ul className={`menu__box" ${show ? 'hidden': ''}`}>
      <li><Link to="/where" className="menu__item" >Dove siamo?</Link></li>
      <li><Link to="/photogrid" className="menu__item" >I nostri lavori</Link></li>
      <li><Link to="/me" className="menu__item" >Chi sono?</Link></li>
      <li><Link to="/businesscard" className="menu__item" >Contattaci</Link></li>
      <li><Link to="/contact" className="menu__item" >Preventivo</Link></li>
    </ul>
  </div>
</Burger>
 );
}

export default Ham;
