/* eslint-disable react/jsx-props-no-spreading */
/* eslint-disable camelcase */
import React, { useState } from 'react';
import emailjs from '@emailjs/browser';
import { useForm } from 'react-hook-form';
import styled from "styled-components";
import load from "../load.svg"

const FormWrap = styled.div`.container{
	text-align: center;
	height: 600px;
  background:#939597;
  width:80%;
  display:grid;
  grid-template-columns:60% 40%;
  position:relative;
  left:4.8%;
  margin:4%;
  border-radius:8px;
  padding:2%;
}
.btn-container {
	position: relative;
	left:4%;

	width: 20%;
	margin-top: 2%;
}
.btn-txt {
	margin: 0;
	position: relative;
	padding: 0%;
	font-size: 1.2rem;
}
.submit {
	background-color: #f6f6f6;
	color: #1d1d1d;
	border: #1d1d1d solid;

	width: 86%;
	height: 40px;
	position: relative;
	font-size: 1.6rem;
  padding:2%;
  top:20px;
	left: 2%;
	border-radius: 8px;
}
.submit:active {
	color: #f6f6f6;
	background-color: black;
	border: #1d1d1d solid;
}
.text {
	margin-bottom: 0%;
  height:80px;
	margin-top: 0%;
	margin-left: 4%;
}
.input {
	display:grid;
	grid-template-rows: 20% 20% 20%;

  text-align: left;
	position: relative;
  top:-20px;
	left:1%;
	width: 100%;
}
.style {
	background: none;
	color: #1d1d1d;
	border:none;
	margin: 2%;
  margin-top:8%;
	inline-size: 40%;
	height:12px;
	padding: 2%;
	font-family: "Poppins";
	font-size: 16pt;
  width:80%;
	border-bottom: solid 0.08em  #f6f6f6;
}
.formtitle
{ color:#f6f6f6;
  margin:0;
  font-size:1.8rem;

}
.formdesc
{
  margin:0;
}
.style::placeholder
{
	color:#f6f6f6;;

	font-size: 1.2rem;
}
.img-load{
  height:80%;
  width:80%;
  position:relative;
  top:10%;
}`

function Submit() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const onSubmit = data => {
    setOne(data);
    sendEmail();
  }


  const [one, setOne] = useState({
    nome: ' ',
    email: 'paolo.uni.95@gmail.com',
    message: ' ',
    emaildata: ' ',
  });
  // const [list, setList] = useState ([]);
  const sendEmail = (e) => {
    e.preventDefault();
    emailjs.sendForm('service_6ckdu5q', 'template_ddnas2b', one, 'user_COHw7BwAfI4bYoD3GOuRd')
      .then((result) => {
        console.log(result.text);
        alert('form inviato');
      }, (error) => {
        console.log(error.text);
      });
  }

  // const handleClick = (e) => {
  //   const { name, value } = e.target;
  //   setOne({ ...one, [name]: value });
  //   console.log(value);
  // };

  // useEffect(() => {
  //   const { log } = console;
  //   log(one);
  // }, [one]);
  return (
    <FormWrap>
      <div className="container">
          <form onSubmit={handleSubmit(onSubmit)} className="input">
         <div className="text">
          <h2 className="txt formtitle">Vuoi prenotare un servizio fotografico?</h2>
          <h4 className="txt formdesc">Un nostro impiegato ti contatterà in 48 ore</h4>
        </div>

      <input {...register("name", { required: true } )} className="style"  placeholder="Nome" />

      <input {...register("Email", { required: true })} className="style" placeholder="Email"/>

      {errors.exampleRequired && <span>This field is required</span>}

      <input {...register("Messagio", { required: true })} className="style" placeholder="Messaggio" />
      {errors.exampleRequired && <span>This field is required</span>}
      <button type="submit" className="submit" onClick={handleSubmit} > {} </button>


    </form>
        <div className="svg-container">
          <img src={load} alt="load" className="img-load"/>
          </div>



      </div>
    </FormWrap>
  );
}

export default Submit;
