import React, { useEffect, useState } from "react";




const Submit = () => {



const [one, setOne] = useState ({nome : " ",
email:" ", message : " "});
const [list, setList] = useState ([]);

const gestiscidati = (e) => {
    e.preventDefault();
    if (one.nome && one.email && one.message)
    {

    setList ([...list, 
    {
        id: new Date(Date.now()).getTime().toString(),
        ...one
    },])
    setOne ({
        nome: "",
        email: "",
        message: ""

    })
 alert(one.nome)
}
else 
alert ("riempi il form coglione")
}
const handleClick = (e) => {
    const { name, value} = e.target
    setOne({...one, [name]: value})
    console.log(value)
}

useEffect (()=> {
    const {log} = console;
    log(one);
}, [one]);
return(
    <article className="form" > 
<div className = "container"  >
                <div className="text">
                <h2 className = "txt formtitle">Vuoi prenotare un servizio fotografico?</h2>
                <h4 className= "txt formdesc">Un nostro impiegato ti contatter√† in 48 ore</h4>
                <form className = "input" onSubmit = {gestiscidati} className="messaggi">
                    <label htmlFor= "nome">
                    <input type = "text" id = "nome" name = "nome" placeholder= "nome" className = "style nome" value = {one.nome} onChange = {handleClick}/>
                    </label>
                    <label htmlFor = "email">
                    <input type = "email" id = "email" placeholder= "e-mail" name = "email" value = {one.email} onChange = {handleClick} className = "style email"/>
                    </label>
                    <label htmlFor = "message">
                    <input type = "textarea" id = "message" placeholder= "Lascia qua il tuo messaggio" name ="message" value  = {one.message} onChange = {handleClick} className = "style messaggio"/>
                    </label>
                    <button type = "submit" className = "style " > Manda, testina!</button>

                </form>
                </div>
</div>
</article>
)
}

export default Submit;
