  font-family:"Poppins";
  font-size: 32px;
  transition-duration: .25s;
}
}
@media screen and (max-width:840px) and  (min-width:440px) {
  width:104%;
  .menu__box{
    left:100%;

  }
  #menu__toggle:checked ~ .menu__box {
    left:4%;!important;
    margin-left:-110px;

  }
  .menu__btn {
    position: fixed;
    top: 40px;
    left:20px;
    margin-left:80%;
    width: 46px;
    height: 46px;
    cursor: pointer;
    border:none;
    z-index: 1;
  }
  .menu__item
  {font-size:1.2rem;}
}
`

function Ham(){
 return (  
<Burger>
< div className="hamburger-menu">
    <input id="menu__toggle" type="checkbox" />
    <label className="menu__btn" for="menu__toggle">
      <span></span>
    </label>

    <ul className="menu__box">
      <li><Link to="/where" className="menu__item" >Dove siamo?</Link></li>
      <li><Link to="/photogrid" className="menu__item" >I nostri lavori</Link></li>
      <li><Link to="/me" className="menu__item" >Chi sono?</Link></li>
      <li><Link to="/businesscard" className="menu__item" >Contattaci</Link></li>
      <li><Link to="/contact" className="menu__item" >Preventivo</Link></li>
    </ul>
  </div>
</Burger>
 );
}

export default Ham;
