/* eslint-disable camelcase */
import React, { useEffect, useState, useRef } from 'react';
import emailjs from '@emailjs/browser';
import { useForm } from 'react-hook-form';

function Submit() {
  const form = useRef();
  const user_email = 'franco.fontana2010@libero.it';

  const [one, setOne] = useState({
    nome: ' ',
    email: 'franco.fontana2010@libero.it',
    message: ' ',
    emaildata: ' ',
  });
  // const [list, setList] = useState ([]);
  const sendEmail = (e) => {
    if (one.name !== '') {
      e.preventDefault();
      emailjs.sendForm('service_6ckdu5q', 'template_ddnas2b', form.current, 'user_COHw7BwAfI4bYoD3GOuRd')
        .then((result) => {
          console.log(result.text);
          alert('form inviato');
        }, (error) => {
          console.log(error.text);
        });
    } else {
      alert('manda il form completo');
    }
  };
  const handleClick = (e) => {
    const { name, value } = e.target;
    setOne({ ...one, [name]: value });
    console.log(value);
  };

  useEffect(() => {
    const { log } = console;
    log(one);
  }, [one]);
  return (
    <article className="form">
      <div className="container">
        <div className="text">
          <h2 className="txt formtitle">Vuoi prenotare un servizio fotografico?</h2>
          <h4 className="txt formdesc">Un nostro impiegato ti contatterà in 48 ore</h4>
        </div>
        <form className="input" ref={form} onSubmit={sendEmail}>
          <label htmlFor="nome" placeholder="Messaggio" >
            <input type="text" id="name" name="nome" className="style name" value={one.nome} onChange={handleClick} placeholder="Name" />
          </label>
          <label htmlFor="email">
            <input type="text" id="email" name="emaildata" value={one.emaildata} onChange={handleClick} className="style name" placeholder="Messaggio" />
            <input type="email" value={user_email} className="hidden" />
          </label>
          <label htmlFor="message">
            <input type="textarea" placeholder="Messaggio" id="message" name="message" value={one.message} onChange={handleClick} className="style messaggio"  />
          </label>
          <div className="btn-container">
            <button type="submit" className="submit" onClick={sendEmail}>
              {' '}
              <h2 className="btn-txt">Manda form!</h2>
            </button>
          </div>

        </form>

      </div>
    </article>
  );
}

export default Submit;
