import React, { useEffect, useState,useRef } from "react";
import emailjs from '@emailjs/browser'



const Submit = () => {

const form = useRef();
user_email="paolo.uni.95@gmail.com"

const [one, setOne] = useState ({nome : " ",
email:" ", message : " "});
// const [list, setList] = useState ([]);
const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_6ckdu5q', 'template_ddnas2b', form.current, user_email,  'user_COHw7BwAfI4bYoD3GOuRd')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };
// const gestiscidati = (e) => {
