//         ...one
//     },])
//     setOne ({
//         nome: "",
//         email: "",
//         message: ""

//     })
const handleClick = (e) => {
    const { name, value} = e.target
    setOne({...one, [name]: value})
    console.log(value)
}

useEffect (()=> {
    const {log} = console;
    log(one);
}, [one]);
return(
    <article className="form" > 
<div className = "container"  >
                <div className="text">
                <h2 className = "txt formtitle">Vuoi prenotare un servizio fotografico?</h2>
                <h4 className= "txt formdesc">Un nostro impiegato ti contatterà in 48 ore</h4>
                <form className = "input" ref={form} onSubmit = {sendEmail} >
                    <label htmlFor= "nome">
                    <h4 className = "txt label">Nome:</h4>
                    <input type = "text" id = "name" name = "nome"  className = "style name" value = {one.name} onChange = {handleClick}/>
                    </label>
                    <label htmlFor = "email">
                    <h4 className = "txt label" >Email:</h4>
                    <input type = "email" id = "name" name = "email" value = {one.email} onChange = {handleClick} className = "style name"/>
                    </label>
                    <label htmlFor = "message">
                    <h4 className = "txt label">Messaggio:</h4>
                    <input type = "textarea" id = "message"  name ="message" value  = {one.message} onChange = {handleClick} className = "style messaggio"/>
                    </label>
                    <button type = "submit" className = "style submit " > Manda form!</button>

                </form>
                </div>
</div>
</article>
)
}

export default Submit;
